from bs4 import BeautifulSoup

def collect_product_prices_and_names(html):
    soup = BeautifulSoup(html, 'html.parser')
    products = soup.select('.goods-tile')
    results = []
    for product in products:
        name = product.select_one('.goods-tile__title').text.strip()
        price = product.select_one('.goods-tile__price-value').text.strip().replace(',', '').replace('\xa0', '')
        if not price[-1].isdigit():
            price = price[:-1]
        results.append((name, float(price)))
    return results


